SELECT trans.TRANS_DATE as transDate, trans.DEAL_NO as dealNo, trans.AMT_CNY as amtCny, trans.TAX_AMT_CNY as taxAmtCny, trans.BANK_CODE as bankCode, inst.INST_NAME as bankName, vendor.VENDOR_ID as vendorId, vendor.VENDOR_CNAME as vendorName, vendor.VENDOR_TAXNO as vendorTaxNo, trans.BILL_CODE as billCode, trans.BILL_NO as billNo from (select * from (select trans.*,rownum as rowindex from vms_input_trans_info trans where trans.BANK_CODE in
#auth_inst_ids[]#
) trans left join u_base_inst inst on trans.BANK_CODE = inst.INST_ID left join VMS_VENDOR_INFO vendor on trans.VENDOR_ID = vendor.VENDOR_ID

SELECT count(*)
from
(select * from
