from vms_input_trans_info trans where 1 =
1

) trans
left join VMS_VENDOR_INFO vendor on trans.VENDOR_ID =
vendor.VENDOR_ID

)

select bills.bill_id as billId, bills.bill_code as billCode, bills.bill_no as billNo, bills.bill_date as billDate, bills.instcode as instCode, (select INST_NAME from U_BASE_INST UBI where UBI.INST_ID = bills.instcode) as instName, bills.amt_sum as amtSum, bills.tax_amt_sum as taxAmtSum, bills.fapiao_type as faPiaoType, bills.vendor_name as vendorName, bills.vendor_taxno as vendorTaxNo, bills.datastatus as dataStatus, bills.identify_date as identifyDate, bills.scan_date as scanDate from (select * from (select bills.*,rownum as rowindex from VMS_INPUT_INVOICE_INFO bills
