sqlMap.insert:
insert into VMS_INPUT_TRANS_INFO ( DEAL_NO, BILL_CODE, BILL_NO, AMT_CNY, TAX_AMT_CNY, INCOME_CNY, SURTAX1_AMT_CNY, SURTAX2_AMT_CNY, SURTAX3_AMT_CNY, SURTAX4_AMT_CNY, VENDOR_ID, BANK_CODE, REMARK, TRANS_DATE
)values( #dealNo#, #billCode#, #billNo#, #amtCny#, #taxAmtCny#, #incomeCny#, #surtax1AmtCny#, #surtax2AmtCny#, #surtax3AmtCny#, #surtax4AmtCny#, #vendorId#, #bankCode#, #remark#, #transDate#
)

insert into VMS_INPUT_INVOICE_MAIN(
BILL_CODE, BILL_NO, BILL_DATE, VENDOR_NAME, AMT, TAX_AMT, DATA_STATUS
)values(
#billCode#, #billNo#, #billDate#, #vendorName#, #amt#, #taxAmt#, '0'
)

